const skillsData = await client.fetch(skillsQuery);

export default function Skills() {
    const [activeCategory, setActiveCategory] = useState(skillsData[0]?.key || 'frontend');

    const activeCategoryData = skillsData.find((cat: any) => cat.key === activeCategory);

    return (
        <section className="py-6 sm:py-2 md:py-16 lg:py-20">
            <div className="w-full max-w-7xl mx-auto px-4 sm:px-6">
                {/* Header */}
                <div className="flex flex-col items-center text-center gap-2 sm:gap-4 mb-8 sm:mb-16 md:mb-20">
                    <h2 className="font-roobert text-3xl sm:text-4xl md:text-[3.5rem] lg:text-[4.5rem] font-normal leading-tight md:leading-[1.11] text-rich-black">
                        <AnimatedText text="Technical Skills" className="text-center space-x-4" />
                    </h2>
                    <p className="text-sm sm:text-base md:text-lg text-rich-black/60 max-w-2xl leading-relaxed">
                        Technologies and tools I use to craft exceptional digital experiences
                    </p>
                </div>

                {/* Main Content */}
                <div className="flex flex-col gap-6 sm:gap-10 md:gap-12">
                    {/* Category Pills - WRAPPING, NOT SCROLLING */}
                    <div className="flex items-center justify-center">
                        <div className="flex flex-wrap justify-center gap-2 sm:gap-3 max-w-full bg-white p-5 lg:p-2 rounded-sm">
                            {skillsData.map((category: any) => {
                                const isActive = activeCategory === category.key;
                                const IconComponent = iconMap[category.icon] || Code;

                                return (
                                    <button
                                        key={category.key}
                                        onClick={() => setActiveCategory(category.key)}
                                        className={`inline-flex items-center gap-1.5 sm:gap-2 px-3 sm:px-6 py-2 sm:py-3 rounded-sm font-roobert font-medium text-xs sm:text-sm whitespace-nowrap transition-all duration-280 ${isActive
                                            ? 'bg-rich-black text-white shadow-lg'
                                            : 'text-rich-black hover:bg-rich-black/10 bg-rich-black/5'
                                            }`}
                                    >
                                        <IconComponent className="w-3.5 h-3.5 sm:w-5 sm:h-5" />
                                        <span className="hidden sm:inline">{category.title}</span>
                                        <span className="sm:hidden">{category.title.split(' ')[0]}</span>
                                    </button>
                                );
                            })}
                        </div>
                    </div>

                    {/* Skills Content Area */}
                    {activeCategoryData && (
                        <div className="max-w-5xl mx-auto w-full">
                            {/* Category Description */}
                            <div className="text-center mb-6 sm:mb-10 md:mb-12">
                                <p className="text-xs sm:text-sm md:text-base text-rich-black/70 leading-relaxed">
                                    {activeCategoryData.description}
                                </p>
                            </div>

                            {/* Skills Grid - MORE COMPACT */}
                            <div className="grid grid-cols-2 sm:grid-cols-2 lg:grid-cols-3 gap-3 sm:gap-6">
                                {activeCategoryData.skills.map((skill: any, index: number) => {
                                    const SkillIconComponent = iconMap[skill.icon] || Code;

                                    return (
                                        <div
                                            key={skill._key}
                                            className="group relative bg-white border border-rich-black/10 rounded-xl p-4 sm:p-6 md:p-7 hover:border-rich-black/20 hover:shadow-[0_8px_30px_rgb(0,0,0,0.06)] hover:-translate-y-0.5 transition-all duration-280"
                                            style={{
                                                animationDelay: `${index * 60}ms`,
                                                opacity: 0,
                                                animation: 'fadeInUp 0.6s ease-out forwards'
                                            }}
                                        >
                                            <div className="flex flex-col items-center gap-2 sm:gap-3.5">
                                                <div className="w-10 h-10 sm:w-14 sm:h-14 text-rich-black group-hover:scale-105 transition-transform duration-280">
                                                    <SkillIconComponent />
                                                </div>
                                                <h3 className="font-roobert text-[10px] sm:text-sm font-medium text-rich-black text-center leading-tight">
                                                    {skill.name}
                                                </h3>
                                            </div>
                                        </div>
                                    );
                                })}
                            </div>

                            {/* Skills Count */}
                            <div className="text-center mt-6 sm:mt-10 md:mt-12 pt-4 sm:pt-8 border-t border-rich-black/10">
                                <p className="text-xs sm:text-sm text-rich-black/50">
                                    <span className="font-semibold text-rich-black">
                                        {activeCategoryData.skills.length} core technologies
                                    </span>{' '}
                                    in {activeCategoryData.title}
                                </p>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </section>
    );
}